            <a href="javascript:;" onclick="show('com_block')" style="color:#a1a1a1">最新评论</a></div><div id="hot_block"><?php if(is_array($hot_art)): $i = 0; $__LIST__ = $hot_art;if( count($__LIST__)==0 ) : echo "" ;else: foreach($__LIST__ as $key=>$vo): $mod = ($i % 2 );++$i;?><div class="line"><div title="<?php echo ($vo["title"]); ?>"  class="nowrap"><div class="hot_ico"></div><a href="__APP__/article/view/id/<?php echo ($vo["id"]); ?>" title="<?php echo ($vo["title"]); ?>"><?php echo ($vo["title"]); ?></a></div><div style="color:#A1A1A1">浏览次数：<?php echo ($vo["apv"]); ?> 次</div></div><?php endforeach; endif; else: echo "" ;endif; ?></div><!--热门--><div id="top_block" style="display:none"><?php if(is_array($new_art)): $i = 0; $__LIST__ = $new_art;if( count($__LIST__)==0 ) : echo "" ;else: foreach($__LIST__ as $key=>$vo): $mod = ($i % 2 );++$i;?><div class="line"><div title="<?php echo ($vo["title"]); ?>"  class="nowrap"><div class="new_ico"></div><a href="__APP__/article/view/id/<?php echo ($vo["id"]); ?>" title="<?php echo ($vo["title"]); ?>"><?php echo ($vo["title"]); ?></a></div><div style="color:#A1A1A1">浏览次数：<?php echo ($vo["apv"]); ?> 次</div></div><?php endforeach; endif; else: echo "" ;endif; ?></div><!--最新--><div id="mes_block" style="display:none"><?php if(is_array($new_leave)): $i = 0; $__LIST__ = $new_leave;if( count($__LIST__)==0 ) : echo "" ;else: foreach($__LIST__ as $key=>$vo): $mod = ($i % 2 );++$i;?><div class="block" ><div class="left"><img class="avatar" src="__PUBLIC__/images/head/<?php echo ($vo["head"]); ?>" width="32" height="32" /></div><div class="right" title="<?php echo ($vo["content"]); ?>"><?php echo ($vo["nickname"]); ?> 说:<div class="nowrap"><?php echo ($vo["content"]); ?></div></div></div><?php endforeach; endif; else: echo "" ;endif; ?></div><!--留言--><div id="com_block" style="display:none"><?php if(is_array($new_comment)): $i = 0; $__LIST__ = $new_comment;if( count($__LIST__)==0 ) : echo "" ;else: foreach($__LIST__ as $key=>$vo): $mod = ($i % 2 );++$i;?><div class="block" ><div class="left"><img class="avatar" src="__PUBLIC__/images/head/<?php echo ($vo["head"]); ?>" width="32" height="32"/></div><div class="right"><div class="nowrap"><?php echo ($vo["nickname"]); ?> 评论了： <a href="__APP__/article/view/id/<?php echo ($vo["aid"]); ?>" style="color:#f00"  title="<?php echo (getarticlebyid($vo["aid"])); ?>"><?php echo (getarticlebyid($vo["aid"])); ?></a></div><div class="nowrap" title="<?php echo ($vo["content"]); ?>" ><?php echo ($vo["content"]); ?></div></div></div><?php endforeach; endif; else: echo "" ;endif; ?></div><!--评论--></div><div class="rbox_320_bottom"></div></div><!--文章Tab--><script>function showAuto() {
    n = n >= (count - 1) ? 0 : ++n;
    $("#slide_control li").eq(n).trigger('click');
}

function show(obj){
    $('#'+obj).siblings("[class!=title]").css('display','none');
    $('#'+obj).css('display','');
}
var t = n = 0, count = $("#slide_list a").size();
$(function(){
    //slide
    $("#slide_list a:not(:first-child)").hide();
    $("#slide_info").html($("#slide_list a:first-child").find("img").attr('alt'));
    $("#slide_control li:first-child").css({"background-position":"-17px top"});
    $("#slide_info").click(function(){
        window.open($("#slide_list a:first-child").attr('href'), "_blank")
    });
    $("#slide_control li").click(function() {
        var i = $(this).text() - 1;
        n = i;
        if (i >= count) return;
        $("#slide_info").html($("#slide_list a").eq(i).find("img").attr('alt'));
        $("#slide_info").unbind().click(function(){
            window.open($("#slide_list a").eq(i).attr('href'), "_blank");
        })
        $("#slide_list a").filter(":visible").fadeOut(500).parent().children().eq(i).fadeIn(1000);
        $(this).css({"background-position":"-17px top"}).siblings().css({"background-position":"0 top"});
    });
    t = setInterval("showAuto()", 3500);
    $("#slide").hover(function(){
        clearInterval(t)
    }, function(){
        t = setInterval("showAuto()", 3500);
    });
    
    //arttab
    $('.art_tab .title a').click(function(){
        $(this).css({color:"#3C99C9","font-weight":"bolder"});
        $(this).siblings().css({color:"#a1a1a1","font-weight":"normal"});
    });
})
